import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function useDebounced(value, delay) {
  const [debounced, setDebounced] = useState(value);
  useEffect(() => {
    const t = setTimeout(() => setDebounced(value), delay);
    return () => clearTimeout(t);
  }, [value, delay]);
  return debounced;
}

function App() {
  const [input, setInput] = useState('');
  const debounced = useDebounced(input, 300);
  const [suggestions, setSuggestions] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(-1);
  const [isAnagram, setIsAnagram] = useState(null);
  const [other, setOther] = useState('');
  const [loading, setLoading] = useState(false);
  const mounted = useRef(true);
  const inputRef = useRef(null);
  const comboInputRef = useRef(null);

  useEffect(() => {
    mounted.current = true;
    return () => { mounted.current = false };
  }, []);

  // keyboard shortcuts: '/' or Ctrl/Cmd+K focus main input
  useEffect(() => {
    function onKey(e) {
      if (e.key === '/') {
        if (document.activeElement === inputRef.current) return;
        e.preventDefault();
        inputRef.current && inputRef.current.focus();
      }
      const isMac = navigator.platform.toUpperCase().indexOf('MAC') >= 0;
      if ((e.ctrlKey && !isMac) || (e.metaKey && isMac)) {
        if (e.key.toLowerCase() === 'k') {
          e.preventDefault();
          inputRef.current && inputRef.current.focus();
        }
      }
    }
    window.addEventListener('keydown', onKey);
    return () => window.removeEventListener('keydown', onKey);
  }, []);

  useEffect(() => {
    // reset selection whenever suggestions change
    setSelectedIndex(-1);
  }, [suggestions]);

  // when selectedIndex changes, ensure item is visible
  useEffect(() => {
    if (selectedIndex >= 0) {
      const el = document.getElementById(`sugg-${selectedIndex}`);
      if (el && typeof el.scrollIntoView === 'function') {
        el.scrollIntoView({ block: 'nearest', inline: 'nearest' });
      }
    }
  }, [selectedIndex]);

  useEffect(() => {
    if (!debounced || debounced.trim().length === 0) {
      setSuggestions([]);
      return;
    }
    setLoading(true);
    fetch(`/api/suggestions?text=${encodeURIComponent(debounced)}&limit=10`)
      .then(r => r.json())
      .then(data => {
        if (!mounted.current) return;
        setSuggestions(data.suggestions || []);
      })
      .catch(() => setSuggestions([]))
      .finally(() => { if (mounted.current) setLoading(false); });
  }, [debounced]);

  function checkAnagram() {
    fetch('/api/areAnagrams', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ input1: input, input2: other })
    })
      .then(r => r.json())
      .then(j => setIsAnagram(Boolean(j.areAnagrams)))
      .catch(() => setIsAnagram(false));
  }

  function applySuggestion(s) {
    setInput(s);
    setSuggestions([]);
    setSelectedIndex(-1);
    // return focus to input
    setTimeout(() => inputRef.current && inputRef.current.focus(), 0);
  }

  function onInputKeyDown(e) {
    if (suggestions.length === 0) return;
    if (e.key === 'ArrowDown') {
      e.preventDefault();
      setSelectedIndex(i => Math.min(i + 1, suggestions.length - 1));
    } else if (e.key === 'ArrowUp') {
      e.preventDefault();
      setSelectedIndex(i => Math.max(i - 1, 0));
    } else if (e.key === 'Enter') {
      // if a suggestion is highlighted, pick it
      if (selectedIndex >= 0 && selectedIndex < suggestions.length) {
        e.preventDefault();
        applySuggestion(suggestions[selectedIndex]);
      } else {
        // otherwise, maybe run the check
        // fallthrough: allow form submit behavior if needed
      }
    } else if (e.key === 'Escape') {
      setSuggestions([]);
      setSelectedIndex(-1);
    } else if (e.key === 'Tab') {
      if (selectedIndex >= 0 && selectedIndex < suggestions.length) {
        applySuggestion(suggestions[selectedIndex]);
        // allow tab to continue focusing
      }
    }
  }

  return (
    <div className="App" style={{ fontFamily: 'Arial, sans-serif', padding: 20 }}>
      <h2>Anagram Checker</h2>
      <div style={{ maxWidth: 680 }}>
        <label htmlFor="anagram-input">Input (type to get suggestions)</label>
        <input
          id="anagram-input"
          ref={inputRef}
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={onInputKeyDown}
          placeholder="Type a word or phrase..."
          aria-autocomplete="list"
          aria-controls="suggestion-list"
          aria-activedescendant={selectedIndex >= 0 ? `sugg-${selectedIndex}` : undefined}
          style={{ width: '100%', padding: '8px 10px', fontSize: 16, boxSizing: 'border-box' }}
        />
        {loading && <div style={{ marginTop: 6 }}>Loading suggestions...</div>}
        {suggestions.length > 0 && (
          <ul role="listbox" id="suggestion-list" style={{ listStyle: 'none', padding: 8, marginTop: 6, border: '1px solid #eee', borderRadius: 6 }}>
            {suggestions.map((s, i) => (
              <li
                id={`sugg-${i}`}
                key={i}
                role="option"
                aria-selected={i === selectedIndex}
                className={`suggestion-item ${i === selectedIndex ? 'highlight' : ''}`}
                style={{ padding: '6px 8px', cursor: 'pointer' }}
                onMouseEnter={() => setSelectedIndex(i)}
                onMouseLeave={() => setSelectedIndex(-1)}
                onClick={() => applySuggestion(s)}
              >{s}</li>
            ))}
          </ul>
        )}

        <div style={{ height: 16 }} />
        <label htmlFor="compare-input">Compare with (other string)</label>
        <input id="compare-input" value={other} onChange={e => setOther(e.target.value)} placeholder="Second string to compare..." style={{ width: '100%', padding: '8px 10px', fontSize: 16 }} />
        <div style={{ marginTop: 12 }}>
          <button onClick={checkAnagram} style={{ padding: '8px 12px', fontSize: 16 }}>Check Anagram</button>
        </div>
        {isAnagram !== null && (
          <div style={{ marginTop: 12, padding: 10, background: isAnagram ? '#e6f9ec' : '#ffe6e6', borderRadius: 6 }}>
            Result: <strong>{isAnagram ? 'Anagrams' : 'Not anagrams'}</strong>
          </div>
        )}

        <div style={{ marginTop: 24 }}>
          <h4>Quick admin sample</h4>
          <button onClick={() => fetch('/api/admin/words?limit=20').then(r => r.json()).then(d => alert(JSON.stringify(d.rows, null, 2))).catch(e => alert('failed'))} style={{ padding: '6px 10px' }}>Show 20 words</button>
        </div>

        <div style={{ marginTop: 24 }}>
          <h3>Find combination anagrams (single input)</h3>
          <input ref={comboInputRef} placeholder="Enter letters or phrase" style={{width: '100%', padding: '8px 10px', fontSize:16}} id="combo-input" />
          <div style={{marginTop:8}}>
            <label style={{marginRight:8}}>Max words</label>
            <select id="combo-maxwords" defaultValue={2} style={{marginRight:12}}>
              <option value={1}>1</option>
              <option value={2}>2</option>
              <option value={3}>3</option>
            </select>
            <label style={{marginRight:8}}>Max results</label>
            <input id="combo-maxresults" defaultValue={20} type="number" style={{width:80}} />
            <button style={{marginLeft:12}} onClick={() => {
              const text = document.getElementById('combo-input').value;
              const maxWords = parseInt(document.getElementById('combo-maxwords').value || '2');
              const maxResults = parseInt(document.getElementById('combo-maxresults').value || '20');
               fetch(`/api/combinations?text=${encodeURIComponent(text)}&maxWords=${maxWords}&maxResults=${maxResults}`)
                 .then(r=>r.json()).then(j=>{
                   if (j.combinations && j.combinations.length) {
                     alert('Found combos:\n' + j.combinations.join('\n'));
                   } else {
                     alert('No combinations found');
                   }
                 }).catch(()=>alert('failed'))
             }}>Find</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
